import{_ as d,e as t}from"./app.cc35b0f6.js";const a={},r=t('<h1 id="\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357-\u7B80\u7248" tabindex="-1"><a class="header-anchor" href="#\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357-\u7B80\u7248" aria-hidden="true">#</a> \u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357(\u7B80\u7248)</h1><p><strong>\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357\u7F16\u5199\u4E13\u5BB6\u7EC4</strong></p><p>[\u6458\u8981] \u5FC3\u7535\u56FE\u4F5C\u4E3A\u4E34\u5E8A\u6700\u5E38\u89C1\u7684\u8BCA\u65AD\u5DE5\u5177\u4E4B\u4E00,\u5E94\u7528\u8303\u56F4\u4E0D\u65AD\u6269\u5927,\u73B0\u5DF2\u6210\u4E3A\u201C\u8840\u3001\u5C3F\u3001\u4FBF\u3001\u5F71\u50CF\u5B66\u3001\u5FC3\u7535\u56FE\u201D\u4E94\u5927\u5E38\u89C4\u68C0\u67E5\u4E4B\u4E00,\u7279\u522B\u662F\u5BF9\u67D0\u4E9B\u5FC3\u8840\u7BA1\u75BE\u75C5\u5982:\u6162\u6027\u7F3A\u8840\u6027\u5FC3\u810F\u75C5\u3001\u6025\u6027\u51A0\u8109\u7EFC\u5408\u5F81\u3001\u5FC3\u808C\u708E\u3001\u5FC3\u5305\u708E\u3001\u80BA\u6813\u585E\u4EE5\u53CA\u5FC3\u5F8B\u5931\u5E38\u7B49\u6709\u786E\u8BCA\u4EF7\u503C\u3002\u6B64\u5916,\u5FC3\u7535\u56FE\u5728\u9057\u4F20\u6027\u79BB\u5B50\u901A\u9053\u75BE\u75C5\u3001\u5FC3\u810F\u7ED3\u6784\u5F02\u5E38\u3001\u7535\u89E3\u8D28\u7D0A\u4E71\u7B49\u7684\u8BCA\u65AD\u4E2D\u4E5F\u5177\u6709\u91CD\u8981\u7684\u8F85\u52A9\u4EF7\u503C\u3002\u540C\u65F6,\u5FC3\u7535\u56FE\u4E5F\u88AB\u7528\u4E8E\u76D1\u6D4B\u6297\u5FC3\u5F8B\u5931\u5E38\u836F\u7269\u5E94\u7528\u7684\u7597\u6548\u4EE5\u53CA\u81F4\u5FC3\u5F8B\u5931\u5E38\u60C5\u51B5\u3001\u8BC4\u4F30\u672F\u524D\u98CE\u9669\u3001\u7B5B\u67E5\u4ECE\u4E8B\u9AD8\u5371\u804C\u4E1A\u6216\u7279\u6B8A\u804C\u4E1A\u4EBA\u7FA4\u7B49\u3002\u56E0\u6B64,\u51C6\u786E\u8BB0\u5F55\u548C\u7CBE\u786E\u5206\u6790\u5FC3\u7535\u56FE\u81F3\u5173\u91CD\u8981\u3002\u968F\u7740\u6570\u5B57\u5316\u6280\u672F\u5FEB\u901F\u5F15\u5165,\u6570\u5B57\u5316\u5FC3\u7535\u56FE\u673A\u7684\u6027\u80FD\u5F97\u5230\u4E86\u6781\u5927\u6539\u8FDB,\u4F46\u65E7\u5F0F\u673A\u578B\u5E76\u672A\u5B8C\u5168\u9000\u51FA\u5E02\u573A,\u7ED9\u5FC3\u7535\u56FE\u64CD\u4F5C\u89C4\u8303\u5316\u7684\u63A8\u8FDB\u5E26\u6765\u56F0\u6270\u3002\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u5E94\u7531\u5FC3\u7535\u56FE\u76F8\u5173\u4E13\u4E1A\u6280\u672F\u4EBA\u5458\u5B8C\u6210,\u89C4\u8303\u5316\u64CD\u4F5C\u662F\u83B7\u5F97\u8D28\u91CF\u5408\u683C\u5FC3\u7535\u56FE\u7684\u524D\u63D0,\u4E5F\u662F\u6B63\u786E\u505A\u51FA\u5FC3\u7535\u56FE\u8BCA\u65AD\u7684\u57FA\u7840\u3002\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357\u9002\u7528\u4E8E\u5FC3\u7535\u56FE\u76F8\u5173\u4E13\u4E1A\u6280\u672F\u4EBA\u5458\u3002</p><p>[\u5173\u952E\u8BCD] \u5FC3\u7535\u56FE;\u9002\u5E94\u8BC1;\u5FC3\u7535\u56FE\u63CF\u8BB0;\u64CD\u4F5C\u6307\u5357 [\u5F15\u7528\u683C\u5F0F] \u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357\u7F16\u5199\u4E13\u5BB6\u7EC4.\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357(\u7B80\u7248)[J].\u5B9E\u7528\u5FC3\u7535\u5B66\u6742\u5FD7,2019,28(1):1-6. \u81EA1901\u5E74Willem Einthoven\u7528\u5F26\u7EBF\u5F0F\u5FC3\u7535\u56FE\u673A\u8BB0\u5F55\u7B2C\u4E00\u4EFD\u5FC3\u7535\u56FE[1-2],\u8FC4\u4ECA\u5FC3\u7535\u56FE\u7684\u53D1\u5C55\u5DF2\u903E\u767E\u5E74\u3002\u4F5C\u4E3A\u4E34\u5E8A\u6700\u5E38\u89C1\u7684\u8BCA\u65AD\u5DE5\u5177\u4E4B\u4E00,\u5E94\u7528\u8303\u56F4\u4E0D\u65AD\u6269\u5927,\u73B0\u5DF2\u6210\u4E3A\u201C\u8840\u3001\u5C3F\u3001\u4FBF\u3001\u5F71\u50CF\u5B66\u3001\u5FC3\u7535\u56FE\u201D\u4E94\u5927\u5E38\u89C4\u68C0\u67E5\u4E4B\u4E00,\u7279\u522B\u662F\u5BF9\u67D0\u4E9B\u5FC3\u8840\u7BA1\u75BE\u75C5\u5982:\u6162\u6027\u7F3A\u8840\u6027\u5FC3\u810F\u75C5\u3001\u6025\u6027\u51A0\u8109\u7EFC\u5408\u5F81[3]\u3001\u5FC3\u808C\u708E\u3001\u5FC3\u5305\u708E\u3001\u80BA\u6813\u585E\u4EE5\u53CA\u5FC3\u5F8B\u5931\u5E38\u7B49\u6709\u786E\u8BCA\u4EF7\u503C\u3002\u6B64\u5916,\u5FC3\u7535\u56FE\u5728\u9057\u4F20\u6027\u79BB\u5B50\u901A\u9053\u75BE\u75C5\u3001\u5FC3\u810F\u7ED3\u6784\u5F02\u5E38\u3001\u7535\u89E3\u8D28\u7D0A\u4E71\u7B49\u7684\u8BCA\u65AD\u4E2D\u4E5F\u5177\u6709\u91CD\u8981\u7684\u8F85\u52A9\u4EF7\u503C\u3002\u540C\u65F6,\u5FC3\u7535\u56FE\u4E5F\u88AB\u7528\u4E8E\u76D1\u6D4B\u6297\u5FC3\u5F8B\u5931\u5E38\u836F\u7269\u5E94\u7528\u7684\u7597\u6548\u4EE5\u53CA\u81F4\u5FC3\u5F8B\u5931\u5E38\u60C5\u51B5\u3001\u8BC4\u4F30\u672F\u524D\u98CE\u9669\u3001\u7B5B\u67E5\u4ECE\u4E8B\u9AD8\u5371\u804C\u4E1A\u6216\u7279\u6B8A\u804C\u4E1A\u4EBA\u7FA4\u7B49[4-5]\u3002\u56E0\u6B64,\u51C6\u786E\u8BB0\u5F55\u548C\u7CBE\u786E\u5206\u6790\u5FC3\u7535\u56FE\u81F3\u5173\u91CD\u8981\u30021992\u5E74,\u7F8E\u56FD\u5FC3\u810F\u534F\u4F1A(American Heart Association,AHA)\u3001\u7F8E\u56FD\u5FC3\u810F\u75C5\u5B66\u4F1A(American College of Cardiology,ACC)\u8054\u5408\u53D1\u5E03\u4E86\u5FC3\u7535\u56FE\u5E94\u7528\u6307\u5357[6],\u8BE5\u6307\u5357\u4E3B\u8981\u56F4\u7ED5\u5FC3\u7535\u56FE\u68C0\u67E5\u9002\u5E94\u8BC1\u8FDB\u884C\u4E86\u8BE6\u7EC6\u9610\u8FF0,\u660E\u786E\u4E86\u5FC3\u7535\u56FE\u6280\u672F\u7684\u9002\u7528\u8303\u56F4\u3002AHA/ACCF/HRS\u53D1\u5E03\u7684\u5FC3\u7535\u56FE\u6807\u51C6\u5316\u4E0E\u89E3\u6790\u7684\u5EFA\u8BAE\u4E0E\u4E34\u5E8A\u5E94\u7528\u56FD\u9645\u6307\u5357,\u7B2CI\u548C\u7B2CII\u90E8\u5206\u9610\u8FF0\u4E86\u5FC3\u7535\u56FE\u6280\u672F\u5F62\u6210\u539F\u7406\u5E76\u89C4\u8303\u4E86\u5FC3\u7535\u56FE\u8BCA\u65AD\u672F\u8BED[7-8]\u3002 1928\u5E74\u8463\u627F\u7405\u6559\u6388\u5F15\u8FDB\u4E86\u4E24\u53F0Cambridge\u516C\u53F8\u751F\u4EA7\u7684\u5FC3\u7535\u56FE\u673A,\u5F00\u521B\u4E86\u6211\u56FD\u5FC3\u7535\u56FE\u5E94\u7528\u7684\u5148\u6CB3,\u5F53\u65F6\u7684\u5FC3\u7535\u56FE\u673A\u6BD4\u8F83\u7B28\u91CD,\u4E34\u5E8A\u5E94\u7528\u6709\u9650\u300220\u4E16\u7EAA50\u5E74\u4EE3,\u9EC4\u5B9B\u6559\u6388\u6539\u826F\u4E86\u65E7\u5F0F\u5FC3\u7535\u56FE\u673A,\u63A8\u5E7F\u4E86\u56FD\u5185\u6807\u51C612\u5BFC\u8054\u5FC3\u7535\u56FE\u68C0\u67E5\u65B9\u6CD5\u3002\u968F\u7740\u6570\u5B57\u5316\u6280\u672F\u5FEB\u901F\u5F15\u5165,\u6570\u5B57\u5316\u5FC3\u7535\u56FE\u673A\u7684\u6027\u80FD\u5F97\u5230\u4E86\u6781\u5927\u6539\u8FDB,\u4F46\u65E7\u5F0F\u673A\u578B\u5E76\u672A\u5B8C\u5168\u9000\u51FA\u5E02\u573A,\u7ED9\u5FC3\u7535\u56FE\u64CD\u4F5C\u89C4\u8303\u5316\u7684\u63A8\u8FDB\u5E26\u6765\u56F0\u6270\u3002\u9274\u4E8E\u6B64,\u6211\u4EEC\u7EC4\u7EC7\u56FD\u5185\u5FC3\u7535\u5B66\u9886\u57DF\u7684\u4E13\u5BB6\u5171\u540C\u64B0\u5199\u4E86\u300A\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357(\u7B80\u7248)\u300B,\u73B0\u4E88\u53D1\u5E03\u3002 \u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u5E94\u7531\u5FC3\u7535\u56FE\u76F8\u5173\u4E13\u4E1A\u6280\u672F\u4EBA\u5458\u5B8C\u6210,\u89C4\u8303\u5316\u64CD\u4F5C\u662F\u83B7\u5F97\u8D28\u91CF\u5408\u683C\u5FC3\u7535\u56FE\u7684\u524D\u63D0,\u4E5F\u662F\u6B63\u786E\u505A\u51FA\u5FC3\u7535\u56FE\u8BCA\u65AD\u7684\u57FA\u7840\u3002 \u300A\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u64CD\u4F5C\u6307\u5357(\u7B80\u7248)\u300B\u9002\u7528\u4E8E\u5FC3\u7535\u56FE\u76F8\u5173\u4E13\u4E1A\u6280\u672F\u4EBA\u5458\u3002</p><h2 id="_1-\u5FC3\u7535\u56FE\u68C0\u67E5\u9002\u5E94\u8BC1" tabindex="-1"><a class="header-anchor" href="#_1-\u5FC3\u7535\u56FE\u68C0\u67E5\u9002\u5E94\u8BC1" aria-hidden="true">#</a> 1 \u5FC3\u7535\u56FE\u68C0\u67E5\u9002\u5E94\u8BC1</h2><h3 id="_1-1\u8BC1\u5B9E\u60A3\u6709\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" tabindex="-1"><a class="header-anchor" href="#_1-1\u8BC1\u5B9E\u60A3\u6709\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" aria-hidden="true">#</a> 1.1\u8BC1\u5B9E\u60A3\u6709\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005</h3><p>\u5FC3\u8840\u7BA1\u75BE\u75C5\u6307\u76F4\u63A5\u6216\u95F4\u63A5\u7D2F\u53CA\u5FC3\u810F\u3001\u5FC3\u5305\u6216\u8840\u7BA1\u7684\u75BE\u75C5\u3002\u76F4\u63A5\u7D2F\u53CA\u7684\u5982\u51A0\u72B6\u52A8\u8109\u75BE\u75C5\u3001\u5FC3\u808C\u75C5\u3001\u5FC3\u810F\u74E3\u819C\u75C5\u3001\u5148\u5929\u6027\u5FC3\u810F\u75C5\u3001\u5FC3\u5F8B\u5931\u5E38\u7B49;\u95F4\u63A5\u7D2F\u53CA\u7684\u5982\u9AD8\u8840\u538B\u3001\u80BA\u52A8\u8109\u9AD8\u538B\u3001\u7CD6\u5C3F\u75C5\u3001\u7532\u72B6\u817A\u529F\u80FD\u5F02\u5E38\u4EE5\u53CA\u7535\u89E3\u8D28\u7D0A\u4E71\u7B49\u3002 \u5FC3\u529F\u80FD\u4E0D\u5168\u6307\u7531\u4E8E\u5404\u79CD\u539F\u56E0\u9020\u6210\u5FC3\u808C\u7684\u6536\u7F29\u3001\u8212\u5F20\u529F\u80FD\u4E0B\u964D,\u4F7F\u5FC3\u810F\u5C04\u8840\u51CF\u5C11,\u4F34\u6216\u4E0D\u4F34\u4E34\u5E8A\u75C7\u72B6\u3002</p><h3 id="_1-2\u7591\u4F3C\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" tabindex="-1"><a class="header-anchor" href="#_1-2\u7591\u4F3C\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" aria-hidden="true">#</a> 1.2\u7591\u4F3C\u5FC3\u8840\u7BA1\u75BE\u75C5\u6216\u5FC3\u529F\u80FD\u4E0D\u5168\u8005</h3><p>\u7591\u4F3C\u5FC3\u8840\u7BA1\u75BE\u75C5\u6307\u5B58\u5728\u5FC3\u8840\u7BA1\u76F8\u5173\u7684\u5F02\u5E38\u75C7\u72B6\u3001\u4F53\u5F81\u6216\u5FC3\u8840\u7BA1\u76F8\u5173\u7684\u5F71\u50CF\u5B66\u3001\u5B9E\u9A8C\u5BA4\u68C0\u67E5\u5F02\u5E38\u4EE5\u53CA\u5FC3\u8840\u7BA1\u75BE\u75C5\u9AD8\u5371\u4EBA\u7FA4\u3002\u5F02\u5E38\u75C7\u72B6\u5305\u62EC\u80F8\u75DB\u3001\u80F8\u95F7\u3001\u6C14\u4FC3\u3001\u5FC3\u60B8\u3001\u6655\u53A5\u3001\u54B3\u55FD\u3001\u54AF\u8840\u7B49;\u5F02\u5E38\u4F53\u5F81\u5305\u62EC\u8840\u538B\u5347\u9AD8\u6216\u964D\u4F4E\u3001\u5FC3\u810F\u6269\u5927\u3001\u5FC3\u810F\u6742\u97F3\u3001\u5FC3\u5305\u6469\u64E6\u97F3\u3001\u5FC3\u7387\u5F02\u5E38\u3001\u5FC3\u5F8B\u4E0D\u89C4\u5219\u3001\u6C34\u80BF\u7B49;\u5F71\u50CF\u5B66\u5F02\u5E38\u5305\u62EC\u5FC3\u5F71\u589E\u5927\u6216\u7F29\u5C0F\u3001\u5FC3\u5F71\u8FB9\u7F18\u50F5\u786C\u6216\u4E0D\u89C4\u5219\u3001\u5FC3\u5F71\u70E7\u74F6\u6837\u6539\u53D8\u3001\u5FC3\u5F71\u9774\u6539\u53D8\u3001\u5FC3\u5F71\u640F\u52A8\u5F02\u5E38\u3001\u5FC3\u8840\u7BA1\u9020\u5F71\u6709\u53EF\u7591\u75C5\u7406\u6539\u53D8\u3001\u80BA\u7600\u8840\u3001\u809D\u813E\u80BF\u5927\u7B49;\u5B9E\u9A8C\u5BA4\u68C0\u67E5\u5F02\u5E38\u5305\u62EC\u5FC3\u808C\u9176\u8C31\u6216\u5FC3\u808C\u635F\u4F24\u6807\u5FD7\u7269\u53EF\u7591\u5F02\u5E38\u6216\u52A8\u6001\u6539\u53D8\u3001\u8D2B\u8840\u3001\u708E\u6027\u6307\u6807\u5F02\u5E38\u3001\u8840\u7CD6\u5F02\u5E38\u3001\u8840\u8102\u5F02\u5E38\u7B49;\u5FC3\u8840\u7BA1\u75BE\u75C5\u9AD8\u5371\u4EBA\u7FA4\u7537\u6027\u6216\u5973\u6027\u7EDD\u7ECF\u671F\u540E\u3001\u5438\u70DF\u3001\u7CD6\u5C3F\u75C5\u3001\u9AD8\u8840\u538B\u3001\u80A5\u80D6\u3001\u8102\u4EE3\u8C22\u7D0A\u4E71\u3001\u80BE\u810F\u75BE\u75C5\u3001\u6709\u5FC3\u8840\u7BA1\u75BE\u75C5\u5BB6\u65CF\u53F2\u53CA\u4F5C\u606F\u4E0D\u89C4\u5F8B\u8005\u7B49\u3002</p><h3 id="_1-3\u65E0\u5FC3\u8840\u7BA1\u75BE\u75C5\u53CA\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" tabindex="-1"><a class="header-anchor" href="#_1-3\u65E0\u5FC3\u8840\u7BA1\u75BE\u75C5\u53CA\u5FC3\u529F\u80FD\u4E0D\u5168\u8005" aria-hidden="true">#</a> 1.3\u65E0\u5FC3\u8840\u7BA1\u75BE\u75C5\u53CA\u5FC3\u529F\u80FD\u4E0D\u5168\u8005</h3><p>\u5FC3\u7535\u56FE\u5F02\u5E38\u53D1\u751F\u7387\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\u5448\u6307\u6570\u589E\u957F,\u63A8\u8350\u6210\u5E74\u4EBA\u6BCF\u5E74\u81F3\u5C11\u5E94\u8FDB\u884C\u4E00\u6B21\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u3002 \u5FC3\u7535\u56FE\u68C0\u67E5\u65E0\u521B\u3001\u64CD\u4F5C\u65B9\u4FBF\u3001\u4EF7\u683C\u4F4E\u5EC9\u3001\u53EF\u91CD\u590D\u6027\u9AD8,\u56E0\u6B64\u65E0\u7EDD\u5BF9\u7981\u5FCC\u8BC1,\u662F\u4E34\u5E8A\u4E0A\u6700\u5E38\u7528\u7684\u68C0\u67E5\u4E4B\u4E00\u3002\u9664\u7279\u6B8A\u60C5\u51B5(III\u5EA6\u76AE\u80A4\u70E7\u4F24\u3001\u4E25\u91CD\u76AE\u80A4\u75BE\u75C5\u7B49)\u65E0\u6CD5\u8FDB\u884C\u68C0\u67E5\u5916,\u6240\u6709\u4EBA\u7FA4\u5747\u53EF\u8FDB\u884C\u5E38\u89C4\u5FC3\u7535\u56FE\u68C0\u67E5\u3002</p><h2 id="_2\u5FC3\u7535\u56FE\u68C0\u67E5\u73AF\u5883\u8981\u6C42" tabindex="-1"><a class="header-anchor" href="#_2\u5FC3\u7535\u56FE\u68C0\u67E5\u73AF\u5883\u8981\u6C42" aria-hidden="true">#</a> 2\u5FC3\u7535\u56FE\u68C0\u67E5\u73AF\u5883\u8981\u6C42</h2><p>\u5FC3\u7535\u56FE\u68C0\u67E5\u5E94\u5728\u5BBD\u655E\u3001\u660E\u4EAE\u3001\u901A\u98CE\u7684\u623F\u95F4,\u8FDC\u79BB\u5927\u578B\u7535\u5668\u8BBE\u5907\u3002\u63A8\u8350\u5BA4\u6E29\u63A7\u5236\u572818~26\xB0C,\u907F\u514D\u8FC7\u51B7\u6216\u8FC7\u70ED,\u7279\u522B\u8981\u907F\u514D\u56E0\u5BD2\u51B7\u6240\u81F4\u7684\u808C\u7535\u5E72\u6270\u3002\u68C0\u67E5\u5E8A\u5BBD\u5EA6\u4E0D\u7A84\u4E8E80cm,\u907F\u514D\u56E0\u4F53\u4F4D\u4E0D\u9002\u3001\u80A2\u4F53\u7D27\u5F20\u5EA6\u589E\u52A0\u800C\u5F15\u8D77\u808C\u7535\u5E72\u6270\u3002\u5BA4\u5185\u4FDD\u6301\u6E05\u6D01\u536B\u751F,\u6BCF\u65E5\u7D2B\u5916\u7EBF\u7167\u5C041h,\u63D0\u5021\u4F7F\u7528\u6E05\u6D01\u65E0\u83CC\u7684\u4E00\u6B21\u6027\u5E8A\u5355\u3001\u88AB\u5957\u3001\u6795\u5957\u7B49,\u4E00\u4EBA\u4E00\u6362,\u4EE5\u9632\u4EA4\u53C9\u611F\u67D3;\u672A\u80FD\u505A\u5230\u4E00\u4EBA\u4E00\u6362,\u4E5F\u5E94\u4FDD\u6301\u7528\u5177\u6E05\u6D01\u65E0\u6C61\u67D3;\u4E00\u65E6\u88AB\u6C61\u67D3\u5E94\u7ACB\u5373\u66F4\u6362\u3002</p><h2 id="_3\u5FC3\u7535\u56FE\u68C0\u67E5\u8BBE\u5907\u8981\u6C42" tabindex="-1"><a class="header-anchor" href="#_3\u5FC3\u7535\u56FE\u68C0\u67E5\u8BBE\u5907\u8981\u6C42" aria-hidden="true">#</a> 3\u5FC3\u7535\u56FE\u68C0\u67E5\u8BBE\u5907\u8981\u6C42</h2><p>\u4E3A\u4FDD\u8BC1\u4F7F\u7528\u5B89\u5168\u4EE5\u53CA\u56FE\u5F62\u8D28\u91CF,\u9700\u4F7F\u7528\u7B26\u5408\u6807\u51C6\u7684\u5FC3\u7535\u56FE\u673A\u3002\u6309\u7167\u6297\u7535\u51FB\u80FD\u529B\u5C06\u533B\u7597\u7535\u5668\u8BBE\u5907\u5206\u4E3AB\u578B\u3001BF\u578B\u548CCF\u578B\u3002\u5176\u4E2DB\u578B\u6307\u5176\u6709\u4E00\u5B9A\u9632\u7535\u51FB\u80FD\u529B,\u4E0E\u60A3\u8005\u63A5\u89E6\u90E8\u4EF6\u4E0D\u7EDD\u7F18\u3002BF\u578B\u6307\u5728B\u578B\u8BBE\u5907\u7684\u57FA\u7840\u4E0A,\u589E\u52A0\u4E86\u4E0E\u60A3\u8005\u63A5\u89E6\u90E8\u4EF6\u7EDD\u7F18\u3002CF\u578B\u6307\u9632\u7535\u51FB\u80FD\u529B\u4F18\u4E8EBF\u578B\u7684\u7535\u5B50\u8BBE\u5907,\u8BBE\u5907\u53EF\u76F4\u63A5\u7528\u4E8E\u5FC3\u810F\u68C0\u67E5\u3002\u6309\u7167\u201C\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u56FD\u5BB6\u6807\u51C6GB10793\uE01189\u5FC3\u7535\u56FE\u673A\u4F7F\u7528\u5B89\u5168\u8981\u6C42\u201D,\u76EE\u524D\u5EFA\u8BAE\u4F7F\u7528CF\u578B\u5FC3\u7535\u56FE\u673A,\u8BE5\u578B\u5FC3\u7535\u56FE\u673A\u673A\u58F3\u6CC4\u6F0F\u7535\u6D41&lt;100\u03BCA\u3002\u5E94\u6309\u89C4\u5B9A\u6821\u51C6\u5FC3\u7535\u56FE\u673A\u5404\u9879\u91CD\u8981\u53C2\u6570\u5747\u5728\u6B63\u5E38\u8303\u56F4\u3002</p><h3 id="_3-1\u7075\u654F\u5EA6" tabindex="-1"><a class="header-anchor" href="#_3-1\u7075\u654F\u5EA6" aria-hidden="true">#</a> 3.1\u7075\u654F\u5EA6</h3><p>\u7075\u654F\u5EA6\u662F\u6307\u5FC3\u7535\u56FE\u673A\u5BF9\u5FC3\u7535\u4FE1\u53F7\u7684\u653E\u5927\u80FD\u529B,\u6307\u8F93\u51651mV\u7535\u538B\u65F6\u63CF\u7B14\u7684\u504F\u8F6C\u5E45\u5EA6,\u5E38\u75281mV\u5B9A\u6807\u7535\u538B\u6765\u8868\u793A,\u5176\u5355\u4F4D\u4E3Amm/mV\u3002\u4E00\u822C\u5C06\u5FC3\u7535\u56FE\u673A\u7075\u654F\u5EA6\u5206\u4E3A\u4E09\u6863:5mm/mV\u300110mm/mV\u548C20mm/mV\u3002\u5FC3\u7535\u56FE\u673A\u7684\u6807\u51C6\u7075\u654F\u5EA6\u4E3A10mm/mV,\u89C4\u5B9A\u6807\u51C6\u7075\u654F\u5EA6\u7684\u76EE\u7684\u662F\u4FBF\u4E8E\u5BF9\u5404\u79CD\u5FC3\u7535\u56FE\u8FDB\u884C\u6BD4\u8F83\u3002\u5F53\u5BFC\u8054\u51FA\u73B0\u6B63\u5411\u6CE2\u7279\u522B\u9AD8\u6216\u8D1F\u5411\u6CE2\u7279\u522B\u6DF1\u65F6,\u53EF\u91C7\u75285mm/mV\u7075\u654F\u5EA6\u6863\u4F4D\u3002\u53CD\u4E4B,\u53EF\u91C7\u752820mm/mV \u7075\u654F\u5EA6\u6863\u4F4D\u3002</p><h3 id="_3-2\u566A\u58F0" tabindex="-1"><a class="header-anchor" href="#_3-2\u566A\u58F0" aria-hidden="true">#</a> 3.2\u566A\u58F0</h3><p>\u566A\u58F0\u662F\u6307\u5FC3\u7535\u56FE\u673A\u5DE5\u4F5C\u65F6,\u7531\u4E8E\u653E\u5927\u5668\u4E2D\u5404\u5143\u4EF6\u5185\u90E8\u7535\u5B50\u4E0D\u89C4\u5219\u70ED\u8FD0\u52A8\u4EA7\u751F\u7684\u4FE1\u53F7\u3002\u5373\u4F7F\u6CA1\u6709\u5FC3\u7535\u4FE1\u53F7\u8F93\u5165,\u4ECD\u80FD\u8F93\u51FA\u4E0D\u89C4\u5219\u5FAE\u5C0F\u6742\u4E71\u6CE2\u3002\u56FD\u9645\u4E0A\u89C4\u5B9A\u566A\u58F0\u503C\u5E94&lt;15\u03BCV\u3002</p><h3 id="_3-3\u8F93\u5165\u963B\u6297" tabindex="-1"><a class="header-anchor" href="#_3-3\u8F93\u5165\u963B\u6297" aria-hidden="true">#</a> 3.3\u8F93\u5165\u963B\u6297</h3><p>\u8F93\u5165\u963B\u6297\u662F\u6307\u5FC3\u7535\u56FE\u673A\u672A\u63A5\u6536\u4FE1\u53F7\u6E90\u65F6,\u5728\u653E\u5927\u5668\u8F93\u5165\u7AEF\u6D4B\u91CF\u5230\u7684\u963B\u6297\u3002\u8F93\u5165\u963B\u6297\u8D8A\u5927,\u8FDB\u5165\u5FC3\u7535\u56FE\u673A\u7684\u4FE1\u53F7\u7535\u538B\u8D8A\u5927,\u6CE2\u5F62\u5931\u771F\u8D8A\u5C0F\u3002\u4E00\u822C\u8981\u6C42\u5FC3\u7535\u56FE\u673A\u653E\u5927\u5668\u7684\u8F93\u5165\u963B\u6297&gt;2.5M\u03A9\u3002</p><h3 id="_3-4\u9891\u7387\u54CD\u5E94" tabindex="-1"><a class="header-anchor" href="#_3-4\u9891\u7387\u54CD\u5E94" aria-hidden="true">#</a> 3.4\u9891\u7387\u54CD\u5E94</h3><p>\u9891\u7387\u54CD\u5E94\u662F\u6307\u5FC3\u7535\u56FE\u673A\u8F93\u5165\u76F8\u540C\u5E45\u503C\u3001\u4E0D\u540C\u9891\u7387\u4FE1\u53F7\u65F6,\u5176\u8F93\u51FA\u4FE1\u53F7\u5E45\u5EA6\u968F\u9891\u7387\u7684\u53D8\u5316\u3002\u9891\u7387\u54CD\u5E94\u8303\u56F4\u786E\u5B9A\u4E86\u6709\u7528\u4FE1\u606F\u7684\u6709\u6548\u8303\u56F4\u3002\u76EE\u524D\u56FD\u5185\u5FC3\u7535\u56FE\u673A\u7684\u57FA\u672C\u8981\u6C42\u4E3A0\uE01005~150Hz,\u8FD9\u4E00\u8303\u56F4\u5DF2\u7ECF\u5F97\u5230\u9A8C\u8BC1\u3002\u5BF9\u4E8E\u5A74\u513F,\u9AD8\u9891\u5E94\u4E3A250Hz\u3002\u6570\u5B57\u5316\u6280\u672F\u5728\u5FC3\u7535\u56FE\u673A\u4E0A\u7684\u5E94\u7528,\u4F7F\u501F\u52A9\u8BA1\u7B97\u673A\u5BF9\u6CE2\u5F62\u8FDB\u884C\u653E\u5927\u3001\u7F29\u5C0F\u4EE5\u53CA\u4FE1\u53F7\u5904\u7406\u53D8\u5F97\u5341\u5206\u65B9\u4FBF,\u5FC3\u7535\u56FE\u673A\u7684\u9891\u54CD\u8303\u56F4\u9002\u5EA6\u6269\u5927,\u4EE5\u83B7\u53D6\u66F4\u591A\u5FC3\u7535\u4FE1\u606F,\u5C06\u662F\u672A\u6765\u9700\u8981\u7814\u7A76\u548C\u518D\u5B9A\u4E49\u7684\u4E00\u4E2A\u95EE\u9898\u3002</p><h3 id="_3-5\u65F6\u95F4\u5E38\u6570" tabindex="-1"><a class="header-anchor" href="#_3-5\u65F6\u95F4\u5E38\u6570" aria-hidden="true">#</a> 3.5\u65F6\u95F4\u5E38\u6570</h3><p>\u65F6\u95F4\u5E38\u6570\u662F\u6307\u4ECE\u5FC3\u7535\u56FE\u673A\u8F93\u5165\u76F4\u6D41\u7535\u65F6,\u5FC3\u7535\u56FE\u673A\u63CF\u8BB0\u7684\u4FE1\u53F7\u5E45\u5EA6\u4ECE100%\u4E0B\u964D\u523037%\u5DE6\u53F3\u6240\u9700\u8981\u7684\u65F6\u95F4\u3002\u76EE\u524D\u8981\u6C42\u5FC3\u7535\u56FE\u673A\u7684\u65F6\u95F4\u5E38\u6570\u22653.2s\u3002### 3.6 \u5171\u6A21\u6291\u5236\u6BD4 \u5171\u6A21\u6291\u5236\u6BD4\u662F\u6307\u5FC3\u7535\u56FE\u673A\u7684\u5FC3\u7535\u4FE1\u53F7\u653E\u5927\u500D\u6570\u4E0E\u5E72\u6270\u548C\u566A\u58F0\u7684\u653E\u5927\u500D\u6570\u7684\u6BD4\u503C\u3002\u5171\u6A21\u6291\u5236\u6BD4\u8D8A\u5927,\u6297\u5E72\u6270\u8D8A\u5F3A,\u5171\u6A21\u6291\u5236\u6BD4\u4E00\u822C\u7528\u5206\u8D1DdB\u8868\u793A,\u76EE\u524D\u8981\u6C42\u5404\u5BFC\u8054\u7684\u5171\u6A21\u6291\u5236\u6BD4\u226580dB,\u56FD\u9645\u4E0A\u89C4\u5B9A\u2265100dB\u3002</p><h3 id="_3-7-\u57FA\u7EBF\u7A33\u5B9A\u6027" tabindex="-1"><a class="header-anchor" href="#_3-7-\u57FA\u7EBF\u7A33\u5B9A\u6027" aria-hidden="true">#</a> 3.7 \u57FA\u7EBF\u7A33\u5B9A\u6027</h3><p>\u57FA\u7EBF\u7A33\u5B9A\u6027\u662F\u8861\u91CF\u5FC3\u7535\u56FE\u673A\u672C\u8EAB\u7684\u7A33\u5B9A\u6027\u548C\u5BF9\u7535\u6E90\u7535\u538B\u6CE2\u52A8\u9002\u5E94\u80FD\u529B\u7684\u91CD\u8981\u6307\u6807\u3002\u76EE\u524D\u8981\u6C42\u5FC3\u7535\u56FE\u673A:\u2460 \u7535\u6E90\u7535\u538B\u7A33\u5B9A\u65F6,\u57FA\u7EBF\u7684\u6F02\u79FB\u2264 1mm;\u2461\u7535\u6E90\u7535\u538B\u6CE2\u52A8\u65F6,\u57FA\u7EBF\u7684\u6F02\u79FB\u22641mm;\u2462\u65E0\u4FE1\u53F7\u8F93\u5165\u65F6,\u57FA\u7EBF\u6F02\u79FB\u22642mm;\u2463\u6E29\u5EA6\u57285~40\xB0C\u8303\u56F4\u5185,\u57FA\u7EBF\u5E73\u5747\u6F02\u79FB\u22640\uE0105mm;\u2464\u5FC3\u7535\u56FE\u673A\u786E\u5B9A\u7B49\u7535\u4F4D\u7EBF\u540E,\u572860s\u5185\u57FA\u7EBF\u504F\u79FB\u22645mm\u3002</p><h3 id="_3-8-\u963B\u5C3C-\u4EC5\u9002\u7528\u4E8E\u6A21\u62DF\u5FC3\u7535\u56FE\u673A" tabindex="-1"><a class="header-anchor" href="#_3-8-\u963B\u5C3C-\u4EC5\u9002\u7528\u4E8E\u6A21\u62DF\u5FC3\u7535\u56FE\u673A" aria-hidden="true">#</a> 3.8 \u963B\u5C3C(\u4EC5\u9002\u7528\u4E8E\u6A21\u62DF\u5FC3\u7535\u56FE\u673A)</h3><p>\u963B\u5C3C\u662F\u6307\u5FC3\u7535\u56FE\u673A\u7684\u63CF\u7B14\u5728\u5DE5\u4F5C\u65F6\u6291\u5236\u5176\u632F\u8361\u7684\u53CD\u4F5C\u7528\u529B\u3002\u5F53\u5FC3\u7535\u56FE\u673A\u7684\u963B\u5C3C\u8FC7\u5927\u65F6,\u5FC3\u7535\u56FE\u4E0A\u5FAE\u5C0F\u7684\u6CE2\u5F62\u5E45\u5EA6\u51CF\u5C0F,\u4E25\u91CD\u65F6\u751A\u81F3\u63CF\u8BB0\u4E0D\u51FA;\u800C\u5F53\u963B\u5C3C\u8FC7\u5C0F\u65F6,\u5FC3\u7535\u56FE\u4E0A\u7684\u5C16\u5CF0\u6CE2(\u5982R\u6CE2\u3001S\u6CE2\u7B49)\u5E45\u503C\u4F1A\u589E\u52A0;\u4E5F\u5B58\u5728\u963B\u5C3C\u4E0D\u5747\u7684\u60C5\u51B5\u3002\u56E0\u6B64,\u9700\u5C06\u963B\u5C3C\u8C03\u81F3\u9002\u4E2D\u72B6\u6001,\u4EE5\u4FDD\u6301\u51C6\u786E\u5730\u8BB0\u5F55\u6CE2\u5F62\u3002\u5FC3\u7535\u56FE\u673A\u901A\u7535\u540E,\u8BBE\u7F6E\u8D70\u7EB8\u901F\u5EA6\u4E3A25mm/s,\u6309\u5B9A\u6807\u952E\u8BB0\u5F55\u65B9\u6CE2,\u82E5\u65B9\u6CE2\u6CE2\u5F62\u8F6C\u6298\u89D2\u4E3A\u76F4\u89D2,\u5219\u8868\u660E\u963B\u5C3C\u9002\u5F53;\u82E5\u65B9\u6CE2\u4E0A\u5347\u6216\u4E0B\u964D\u5747\u6709\u7A81\u51FA\u5C16\u6CE2,\u5219\u8868\u660E\u963B\u5C3C\u8FC7\u5C0F;\u65B9\u6CE2\u4E0A\u5347\u53CA\u4E0B\u964D\u90FD\u5448\u5706\u949D\u578B,\u5219\u8868\u660E\u963B\u5C3C\u8FC7\u5927\u3002</p><h3 id="_3-9-\u8D70\u7EB8\u901F\u5EA6" tabindex="-1"><a class="header-anchor" href="#_3-9-\u8D70\u7EB8\u901F\u5EA6" aria-hidden="true">#</a> 3.9 \u8D70\u7EB8\u901F\u5EA6</h3><p>\u8D70\u7EB8\u901F\u5EA6\u662F\u6307\u5FC3\u7535\u56FE\u7EB8\u7684\u8FD0\u884C\u901F\u5EA6,\u5355\u4F4D\u4E3Amm/s,\u5FC3\u7535\u56FE\u673A\u8D70\u7EB8\u901F\u5EA6\u670912.5mm/s\u300125mm/s\u300150mm/s\u548C100mm/s\u56DB\u6863\u9009\u62E9,\u6B63\u5E38\u5E94\u8BBE\u7F6E\u8D70\u7EB8\u901F\u5EA6\u4E3A25mm/s\u3002\u5982\u9700\u653E\u5927\u5FC3\u7535\u56FE\u6CE2\u5F62\u95F4\u671F\u7684\u5206\u8FA8\u7A0B\u5EA6,\u53EF\u8C03\u5FEB\u8D70\u7EB8\u901F\u5EA6\u4E3A50mm/s\u6216100mm/s;\u53CD\u4E4B,\u53EF\u8C03\u51CF\u8D70\u7EB8\u901F\u5EA6\u4E3A12.5mm/s\u3002</p><h3 id="_3-10-\u653E\u5927\u7CFB\u7EDF\u7684\u5BF9\u79F0\u6027" tabindex="-1"><a class="header-anchor" href="#_3-10-\u653E\u5927\u7CFB\u7EDF\u7684\u5BF9\u79F0\u6027" aria-hidden="true">#</a> 3.10 \u653E\u5927\u7CFB\u7EDF\u7684\u5BF9\u79F0\u6027</h3><p>\u5FC3\u7535\u56FE\u673A\u7684\u653E\u5927\u7CFB\u7EDF\u6307\u7684\u662F\u5FC3\u7535\u56FE\u673A\u5BF9\u4EA4\u6D41\u7535\u6CE2\u5BF9\u79F0\u653E\u5927\u7684\u7279\u6027\u3002\u68C0\u6D4B\u653E\u5927\u7CFB\u7EDF\u7684\u65B9\u6CD5\u662F\u5C06\u5FC3\u7535\u56FE\u673A\u7075\u654F\u5EA6\u8C03\u523010mm/mV,\u6253\u5F00\u6807\u51C6\u7535\u538B\u4FE1\u53F7\u6309\u94AE,\u6301\u7EED\u6570\u79D2,\u63CF\u7B14\u8FDE\u7EED\u8BB0\u5F55\u51E0\u4E2A\u5FC3\u52A8\u5468\u671F,\u63CF\u7B14\u4E0A\u4E0B\u63CF\u8BB0\u7684\u5E45\u5EA6\u5E94\u90FD\u4E3A10mm\u3002\u8C03\u6574\u7075\u654F\u5EA6\u523020mm/mV,\u82E5\u8BB0\u5F55\u7B14\u4E0A\u4E0B\u79FB\u52A8\u5E45\u5EA6\u4E3A20mm/mV,\u63D0\u793A\u5FC3\u7535\u56FE\u673A\u653E\u5927\u7CFB\u7EDF\u5BF9\u79F0\u6027\u597D\u3002</p><h2 id="_4-\u5FC3\u7535\u56FE\u68C0\u67E5\u63CF\u8BB0\u8981\u6C42" tabindex="-1"><a class="header-anchor" href="#_4-\u5FC3\u7535\u56FE\u68C0\u67E5\u63CF\u8BB0\u8981\u6C42" aria-hidden="true">#</a> 4 \u5FC3\u7535\u56FE\u68C0\u67E5\u63CF\u8BB0\u8981\u6C42</h2><p>\u6807\u51C612\u5BFC\u8054\u5FC3\u7535\u56FE\u5305\u62EC3\u4E2A\u80A2\u4F53\u5BFC\u8054(I\u3001II\u3001III)\u30013\u4E2A\u52A0\u538B\u5355\u6781\u80A2\u4F53\u5BFC\u8054(aVR\u3001aVL\u3001aVF)\u548C6\u4E2A\u80F8\u58C1\u5BFC\u8054(V1~V6)\u3002\u5FC3\u7535\u56FE\u63CF\u8BB0\u8FC7\u7A0B\u4E2D\u5E38\u4F1A\u51FA\u73B0\u4E0D\u540C\u7A0B\u5EA6\u7684\u5931\u771F,\u5F80\u5F80\u6765\u6E90\u4E8E\u566A\u97F3\u5E72\u6270\u3001\u6E29\u5EA6\u8FC7\u9AD8\u3001\u64CD\u4F5C\u65B9\u6CD5\u4E0D\u5F53\u7B49[9-11]\u3002\u4E25\u683C\u9075\u5FAA\u5FC3\u7535\u56FE\u64CD\u4F5C\u89C4\u8303,\u53EF\u907F\u514D\u5931\u771F,\u63D0\u9AD8\u56FE\u5F62\u8D28\u91CF\u3002</p><h3 id="_4-1-\u51C6\u5907\u5DE5\u4F5C" tabindex="-1"><a class="header-anchor" href="#_4-1-\u51C6\u5907\u5DE5\u4F5C" aria-hidden="true">#</a> 4.1 \u51C6\u5907\u5DE5\u4F5C</h3><p>\u64CD\u4F5C\u8005\u51C6\u5907:\u2460\u5728\u5BF9\u53D7\u68C0\u8005\u8FDB\u884C\u68C0\u67E5\u524D,\u64CD\u4F5C\u8005\u52A1\u5FC5\u8BA4\u771F\u9605\u8BFB\u7533\u8BF7\u5355,\u6838\u5BF9\u597D\u53D7\u68C0\u8005\u59D3\u540D\u3001\u7F16\u53F7\u7B49\u91CD\u8981\u4FE1\u606F\u8D44\u6599,\u6216\u626B\u7801\u786E\u8BA4\u53D7\u68C0\u8005\u8EAB\u4EFD\u4FE1\u606F,\u8BB0\u8F7D\u6216\u5F55\u5165\u8BB0\u5F55\u7CFB\u7EDF;\u5E94\u5FEB\u901F\u4E86\u89E3\u7533\u8BF7\u68C0\u67E5\u7684\u76EE\u7684,\u4E86\u89E3\u5BF9\u63CF\u8BB0\u6709\u65E0\u7279\u6B8A\u8981\u6C42[12];\u2461\u68C0\u67E5\u5FC3\u7535\u56FE\u673A\u5404\u6761\u7EBF\u7F06\u7684\u8FDE\u63A5\u662F\u5426\u6B63\u786E,\u5305\u62EC\u5BFC\u8054\u7EBF\u3001\u7535\u6E90\u7EBF\u7B49,\u5BFC\u8054\u7EBF\u4FDD\u6301\u987A\u7545,\u52FF\u7F20\u7ED5;\u2462\u5BF9\u521D\u6B21\u53D7\u68C0\u8005,\u64CD\u4F5C\u8005\u987B\u4E8B\u5148\u505A\u597D\u6C9F\u901A\u89E3\u91CA\u5DE5\u4F5C\u3002\u53D7\u68C0\u8005\u51C6\u5907:\u2460\u63A5\u53D7\u5FC3\u7535\u56FE\u68C0\u67E5\u524D,\u53D7\u68C0\u8005\u5E94\u7A0D\u4E8B\u4F11\u606F,\u4FDD\u6301\u5E73\u9759,\u907F\u514D\u7D27\u5F20[13];\u2461\u68C0\u67E5\u524D2h\u4E0D\u5438\u70DF,\u4E0D\u996E\u8336\u3001\u5496\u5561\u548C\u9152\u7B49\u523A\u6FC0\u6027\u996E\u54C1[14];\u2462\u53D7\u68C0\u8005\u5C3D\u91CF\u7A7F\u7740\u5BBD\u677E,\u65B9\u4FBF\u5FC3\u7535\u56FE\u68C0\u67E5;\u2463\u653E\u7F6E\u7535\u6781\u90E8\u4F4D\u7684\u76AE\u80A4\u5982\u6709\u6C61\u57A2,\u5E94\u5148\u8FDB\u884C\u76AE\u80A4\u6E05\u6D01;\u2464\u5982\u653E\u7F6E\u7535\u6781\u90E8\u4F4D\u6BDB\u53D1\u8FC7\u591A,\u5219\u5E94\u5243\u9664\u5C40\u90E8\u6BDB\u53D1,\u4EE5\u51CF\u5C11\u7535\u963B\u3002</p><h3 id="_4-2-\u5FC3\u7535\u56FE\u7535\u6781\u5B89\u653E" tabindex="-1"><a class="header-anchor" href="#_4-2-\u5FC3\u7535\u56FE\u7535\u6781\u5B89\u653E" aria-hidden="true">#</a> 4.2 \u5FC3\u7535\u56FE\u7535\u6781\u5B89\u653E</h3><p>\u5728\u88AB\u68C0\u67E5\u8005\u4E24\u624B\u8155\u5173\u8282\u4E0A\u65B9\u53CA\u4E24\u4FA7\u5185\u8E1D\u4E0A\u90E8\u7528\u5BFC\u7535\u4ECB\u8D28,\u6E05\u6D01\u5C40\u90E8\u76AE\u80A4,\u6309\u7167\u987A\u5E8F\u653E\u7F6E\u597D\u7535\u6781\u7247\u548C\u8FDE\u63A5\u5BFC\u8054\u7EBF,\u901A\u5E38\u4E3A\u7EA2\u8272\u5BFC\u8054\u7EBF\u8FDE\u63A5\u53F3\u624B\u624B\u8155\u3001\u9EC4\u8272\u5BFC\u8054\u7EBF\u8FDE\u63A5\u5DE6\u624B\u624B\u8155\u3001\u84DD\u8272(\u6216\u7EFF\u8272)\u5BFC\u8054\u7EBF\u8FDE\u63A5\u5DE6\u4E0B\u811A\u8E1D\u3001\u9ED1\u8272\u5BFC\u8054\u7EBF\u8FDE\u63A5\u53F3\u4E0B\u811A\u8E1D(\u7F3A\u5931\u4E0A\u80A2\u5E94\u653E\u5728\u8BE5\u4FA7\u80A9\u90E8,\u7F3A\u5931\u4E0B\u80A2\u5E94\u653E\u5728\u8BE5\u4FA7\u80A2\u4F53\u7684\u81C2\u90E8),\u80F8\u3001\u80CC\u90E8\u5BFC\u8054\u653E\u7F6E\u4F4D\u7F6E\u5982\u88681\u6240\u793A\u3002\u5176\u4E2DV1~V6\u7684\u7535\u6781\u989C\u8272\u5206\u522B\u4E3A\u7EA2\u3001\u9EC4\u3001\u84DD(\u7EFF)\u3001\u6A59\u3001\u9ED1\u3001\u7D2B\u3002\u67D0\u4E9B\u8FDB\u53E3\u5FC3\u7535\u56FE\u673A\u7684\u5BFC\u8054\u7EBF\u63D2\u4EF6\u4E0A\u6CE8\u6709RA(\u53F3\u4E0A\u80A2)\u3001LA(\u5DE6\u4E0A\u80A2)\u3001LL(\u5DE6\u4E0B\u80A2)\u3001RL(\u53F3\u4E0B\u80A2),\u6309\u7167\u5176\u63D0\u793A\u4E0E\u76F8\u5E94\u7535\u6781\u7247\u8FDE\u63A5\u5373\u53EF,\u4E0D\u53D7\u5BFC\u8054\u7EBF\u989C\u8272\u9650\u5236\u3002\u5973\u6027\u4E73\u623F\u4E0B\u5782\u8005,\u7535\u6781\u7247\u4E0D\u5E94\u8BE5\u653E\u7F6E\u5728\u4E73\u623F\u4E0A,\u5E94\u6258\u8D77\u4E73\u623F\u540E,\u5728\u4E73\u623F\u4E0B\u7F18\u80F8\u58C1\u4E0A\u653E\u7F6E\u76F8\u5E94\u7684\u7535\u6781\u7247;\u4E73\u623F\u5207\u9664\u8005\u5E94\u4E88\u6CE8\u660E\u3002\u5A74\u5E7C\u513F\u5FC3\u7535\u56FE\u68C0\u67E5\u65F6,\u53D6\u4EF0\u5367\u4F4D,\u4FDD\u6301\u5B89\u9759;\u5A74\u5E7C\u513F\u54ED\u95F9\u4E0D\u5408\u4F5C\u65F6,\u53EF\u63D0\u524D\u5E94\u7528\u9547\u9759\u5242\u4F7F\u5176\u5B89\u9759,\u518D\u884C\u68C0\u67E5\u3002\u53E6\u5916,\u5A74\u5E7C\u513F\u80F8\u90E8\u5BFC\u8054\u5E94\u9009\u62E9\u5927\u5C0F\u5408\u9002\u7684\u7535\u6781\u7247,\u4E0D\u4F7F\u7528\u7535\u6781\u5438\u76D8,\u4EE5\u514D\u5BF9\u80F8\u90E8\u9020\u6210\u635F\u4F24\u3002\u5168\u90E8\u68C0\u67E5\u5B8C\u6210\u540E,\u5173\u95ED\u7535\u6E90;\u90E8\u5206\u673A\u578B\u9700\u5C06\u5404\u63A7\u5236\u5668\u65CB\u94AE\u65CB\u81F3\u6700\u4F4E\u70B9\u3002 \u88681 \u80F8\u3001\u80CC\u90E8\u5FC3\u7535\u56FE\u5BFC\u8054\u7535\u6781\u653E\u7F6E\u6807\u51C6\u4F4D\u7F6E</p><table><thead><tr><th>\u5BFC\u8054</th><th>\u6807\u51C6\u4F4D\u7F6E</th></tr></thead><tbody><tr><td>V1</td><td>\u80F8\u9AA8\u53F3\u7F18\u7B2C4\u808B\u95F4\u9699</td></tr><tr><td>V2</td><td>\u80F8\u9AA8\u5DE6\u7F18\u7B2C4\u808B\u95F4\u9699</td></tr><tr><td>V3</td><td>V2\u4E0EV4\u8FDE\u7EBF\u4E2D\u70B9</td></tr><tr><td>V4</td><td>\u5DE6\u9501\u9AA8\u4E2D\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>V5</td><td>\u5DE6\u814B\u524D\u7EBFV4\u540C\u4E00\u6C34\u5E73(\u5373\u7B2C5\u808B\u95F4\u9699)</td></tr><tr><td>V6</td><td>\u5DE6\u814B\u4E2D\u7EBFV4\u3001V5\u540C\u4E00\u6C34\u5E73(\u5373\u7B2C5\u808B\u95F4\u9699)</td></tr><tr><td>V7</td><td>\u5DE6\u814B\u540E\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>V8</td><td>\u5DE6\u80A9\u80DB\u4E0B\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>V9</td><td>\u5DE6\u810A\u67F1\u76D8\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>V3R</td><td>V1\u548CV4R\u8FDE\u7EBF\u4E2D\u70B9</td></tr><tr><td>V4R</td><td>\u53F3\u9501\u9AA8\u4E2D\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>V5R</td><td>\u53F3\u814B\u524D\u7EBF\u7B2C5\u808B\u95F4\u9699</td></tr><tr><td>\u7591\u6709\u6025\u6027\u5FC3\u808C\u6897\u6B7B\u3001\u9996\u6B21\u505A\u5FC3\u7535\u56FE\u68C0\u67E5\u8005,\u5E94\u4E88\u505A18\u5BFC\u8054\u5FC3\u7535\u56FE,\u80F8\u58C1\u5404\u5BFC\u8054\u90E8\u4F4D\u5E94\u505A\u597D\u6807\u8BB0,\u4EE5\u5907\u590D\u67E5\u5B9A\u4F4D\u3002</td><td></td></tr><tr><td>###4.3 \u6B63\u786E\u63CF\u8BB0\u5FC3\u7535\u56FE</td><td></td></tr><tr><td>(1)\u5355\u901A\u9053\u5FC3\u7535\u56FE\u673A,\u63CF\u8BB0\u987A\u5E8F\u4E00\u822C\u4E3AI\u3001II\u3001 III\u3001aVR\u3001aVL\u3001aVF\u3001V1\u3001V2\u3001V3\u3001V4\u3001V5\u3001V6\u3002\u9700\u8981\u63CF\u8BB018\u5BFC\u8054\u5FC3\u7535\u56FE\u65F6,\u63CF\u8BB0\u987A\u5E8F\u4E3AI\u3001II\u3001III\u3001aVR\u3001aVL\u3001aVF\u3001V1\u3001V2\u3001V3\u3001V4\u3001V5\u3001V6\u3001V7\u3001V8\u3001V9\u3001V3R\u3001V4R\u3001V5R\u3002</td><td></td></tr><tr><td>(2)12\u5BFC\u8054\u5FC3\u7535\u56FE\u540C\u6B65\u91C7\u96C6\u5B58\u50A8\u3001\u4E09\u901A\u9053\u540C\u6B65\u6253\u5370\u7684\u5FC3\u7535\u56FE\u5BFC\u8054\u987A\u5E8F\u662F:\u7B2C1\u5217\u81EA\u4E0A\u800C\u4E0B\u4E3AI\u3001II\u3001III,\u7B2C2\u5217\u81EA\u4E0A\u800C\u4E0B\u4E3AaVR\u3001aVL\u3001aVF,\u7B2C3\u5217\u81EA\u4E0A\u800C\u4E0B\u4E3AV1\u3001V2\u3001V3,\u7B2C4\u5217\u81EA\u4E0A\u800C\u4E0B\u4E3AV4\u3001V5\u3001V6\u3002\xB74\xB7 \u5B9E\u7528\u5FC3\u7535\u5B66\u6742\u5FD7\u7B2C28\u5377</td><td></td></tr><tr><td>(3)12\u5BFC\u540C\u6B65\u6253\u5370\u5FC3\u7535\u56FE,\u81EA\u4E0A\u800C\u4E0B\u7684\u5BFC\u8054\u987A\u5E8F\u662FI\u3001II\u3001III\u3001aVR\u3001aVL\u3001aVF\u3001V1\u3001V2\u3001V3\u3001V4\u3001V5\u3001V6\u3002\u5FC3\u7535\u56FE\u91C7\u96C6\u6807\u51C6\u65F6\u95F4\u662F10s\u3002\u5F53\u64CD\u4F5C\u8FC7\u7A0B\u4E2D\u53D1\u73B0\u623F\u6027\u65E9\u640F\u3001\u5FC3\u623F\u98A4\u52A8\u3001\u5BA4\u6027\u65E9\u640F\u3001\u5BA4\u5185\u5DEE\u5F02\u4F20\u5BFC\u7B49\u5F02\u5E38\u5FC3\u7535\u56FE\u73B0\u8C61\u65F6,\u53EF\u5EF6\u957F\u5FC3\u7535\u56FE\u91C7\u96C6\u65F6\u95F4,\u4EE5\u5229\u4E8E\u8BCA\u65AD\u548C\u9274\u522B\u8BCA\u65AD\u3002</td><td></td></tr><tr><td>(4)\u56E0\u4E0D\u540C\u4F53\u4F4D\u5BF912\u5BFC\u8054\u7535\u538B\u53CA\u632F\u5E45\u6709\u5F71\u54CD,\u53D7\u8BD5\u8005\u4E00\u822C\u53D6\u5E73\u5367\u4F4D(\u7279\u6B8A\u60C5\u51B5\u4E0B\u91C7\u53D6\u5750\u4F4D\u3001\u534A\u5750\u4F4D,\u5DE6\u4FA7\u5367\u4F4D\u6216\u53F3\u4FA7\u5367\u4F4D\u7B49\u65F6,\u5E94\u4E88\u6CE8\u660E)\u3002</td><td></td></tr><tr><td>\u5F53\u5BFC\u8054\u4E4B\u95F4\u5FC3\u7535\u56FE\u91CD\u53E0,\u5F71\u54CD\u5206\u6790\u65F6,\u53EF\u5C06\u8BE5\u5BFC\u8054\u7684\u5B9A\u51C6\u7535\u538B\u8BBE\u4E3A5mm/mV,\u8C03\u6574\u5BFC\u8054\u95F4\u8DDD,\u907F\u514D\u5BFC\u8054\u4E4B\u95F4\u51FA\u73B0\u6CE2\u5F62\u91CD\u53E0\u3002</td><td></td></tr></tbody></table>',40);function e(h,i){return r}var m=d(a,[["render",e],["__file","index.html.vue"]]);export{m as default};
