import{_ as e,e as a}from"./app.cc35b0f6.js";const p={},r=a('<h1 id="\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6" aria-hidden="true">#</a> \u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6</h1><p>\u674E\u5C0A\u67F1\uFF0C\u5B59\u7EA2\uFF0C\u5D14\u6587\u535A\uFF0C\u7B49\uFF0E \u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6\uFF3BJ\uFF3D\uFF0E\u534F\u548C\u533B\u5B66\u6742\u5FD7\uFF0C2020\uFF0C11 (6):676-681\uFF0E</p><p>\u3010\u6458\u8981\u3011\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E(coronavirus disease 2019\uFF0CCOVID-19) \u60A3\u8005\u4E2D\uFF0C\u91CD\u75C7\u53CA\u5371\u91CD\u75C7\u60A3\u8005\u5360 5% \uFF5E 10%\u3002\u6B64\u7C7B\u60A3\u8005\u5728\u6CBB\u7597\u4E0E\u5EB7\u590D\u8FC7\u7A0B\u4E2D\uFF0C\u9662\u5185\u8F6C\u8FD0\u662F\u5FC5\u4E0D\u53EF\u5C11\u7684\u73AF\u8282\uFF0C\u4E3A\u4FDD\u969C\u60A3\u8005\u5728\u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u7684\u5B89\u5168\uFF0C\u63D0\u9AD8\u8F6C\u8FD0\u6548\u7387\uFF0CCOVID-19 \u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6\u7F16\u5199\u7EC4\u5728\u56DE\u987E\u5DF2\u6709\u8BC1\u636E\u57FA\u7840\u4E0A\uFF0C\u901A\u8FC7\u591A\u6B21\u8BA8\u8BBA\u5236\u5B9A\u4E86\u300A\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6\u300B\uFF0C\u4EE5\u671F\u4E3A COVID-19 \u91CD\u75C7\u3001\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u63D0\u4F9B\u53C2\u8003\u3002</p><p>\u3010\u5173\u952E\u8BCD\u3011\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E; \u5371\u91CD\u75C7\u60A3\u8005; \u9662\u5185\u8F6C\u8FD0; \u4E13\u5BB6\u5171\u8BC6\u3002</p><p>\u3010Abstract\u3011 Among patients with coronavirus disease 2019 (COVID-19) \uFF0Csevere cases account for 5% to 10%\uFF0E In the process of treatment and rehabilitation of such patients\uFF0CIntra-hospital transport is an indispensable link\uFF0E In order to ensure the safety of patients during the transfer process and improve the efficiency of transfer\uFF0Cthe writing team of expert consensus on intra-hospital transfer of severe patients with COVID-19 reviewed existing evidence and formulated\u201CIntra-hospital Transport of Patients with Severe Coronavirus Disease 2019\u201Dthrough many discussions\uFF0Cwhich provides a reference for the intra-hospital transport of patients with severe COVID-19\uFF0E</p><p>\u3010Key words\u3011 coronavirus disease 2019; critically ill patient; intra-hospital transport; expert consensus</p><p>\u5171\u8BC6\u6784\u5EFA\u65B9\u6CD5\uFF08\u7565\uFF09</p><h2 id="\u5171\u8BC6\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u5171\u8BC6\u5185\u5BB9" aria-hidden="true">#</a> \u5171\u8BC6\u5185\u5BB9</h2><h3 id="_2-1-\u8F6C\u8FD0\u524D" tabindex="-1"><a class="header-anchor" href="#_2-1-\u8F6C\u8FD0\u524D" aria-hidden="true">#</a> 2.1\u2003\u8F6C\u8FD0\u524D</h3><h4 id="_2-1-1-\u8F6C\u8FD0\u4EBA\u5458\u7684\u8D44\u8D28\u4E0E\u914D\u7F6E" tabindex="-1"><a class="header-anchor" href="#_2-1-1-\u8F6C\u8FD0\u4EBA\u5458\u7684\u8D44\u8D28\u4E0E\u914D\u7F6E" aria-hidden="true">#</a> 2.1.1\u2003\u8F6C\u8FD0\u4EBA\u5458\u7684\u8D44\u8D28\u4E0E\u914D\u7F6E</h4><p>(1) \u8F6C\u8FD0\u4EBA\u5458\u5747\u5E94\u719F\u7EC3\u638C\u63E1\u5371\u91CD\u75C7\u60A3\u8005\u62A2\u6551\u6280\u672F\u3001\u65B0\u51A0\u80BA\u708E\u9632\u62A4\u63AA\u65BD\u53CA\u8F6C\u8FD0\u8BBE\u5907\u7684\u5E94\u7528\u7B49\u4E13\u4E1A\u6280\u80FD\u3002</p><p>(2) \u6240\u6709\u8F6C\u8FD0\u4EBA\u5458\u5E94\u63D0\u524D\u63A5\u53D7\u57F9\u8BAD\u5E76\u719F\u77E5\u5371\u91CD\u578B\u65B0\u51A0\u80BA\u708E\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u6D41\u7A0B(\u56FE 1) \u3002</p><p><img src="https://cdn.jsdelivr.net/gh/seasideccm/img/upload/1620336883947-image-20210507053443763.png" alt=""></p><p>(3) \u8F6C\u8FD0\u4EBA\u5458\u5E94\u81F3\u5C11\u5305\u62EC\u533B\u751F\u3001\u62A4\u58EB\u53CA\u534F\u52A9\u4EBA\u5458\u5404 1 \u540D\uFF0C\u8F6C\u8FD0\u524D\u5E94\u6839\u636E\u60A3\u8005\u60C5\u51B5(\u5305\u62EC\u751F\u547D\u4F53\u5F81\u3001\u610F\u8BC6\u72B6\u6001\u3001\u547C\u5438\u652F\u6301\u60C5\u51B5\u3001\u5FAA\u73AF\u652F\u6301\u60C5\u51B5\u3001\u4E34\u5E8A\u72B6\u51B5\u3001\u8F6C\u8FD0\u65F6\u95F4\u7B49) \u8FDB\u884C\u5206\u7EA7\uFF0C\u82E5\u60A3\u8005\u75C5\u60C5\u6781\u5176\u5371\u91CD\uFF0C\u9700\u643A\u5E26\u4F53\u5916\u819C\u80BA\u6C27\u5408\u5668(extracorporeal membrane oxygenation\uFF0CECMO) \u3001\u4E3B\u52A8\u8109\u5185\u7403\u56CA\u53CD\u640F\u6CF5(intraaortic balloon pump\uFF0CIABP) \u7B49\u8BBE\u5907\u65F6\uFF0C\u5E94\u68C0\u6D4B\u8BBE\u5907\u84C4\u7535\u6C60\u7535\u91CF\u662F\u5426\u5145\u8DB3\uFF0C\u5E76\u9002\u5F53\u589E\u52A0\u8F6C\u8FD0\u4EBA\u5458\u6570\u91CF\u3002</p><p>(4) \u8F6C\u8FD0\u4EBA\u5458\u5E94\u8FDB\u884C\u4E8C\u7EA7\u9632\u62A4\uFF0C\u7A7F\u6234\u4E00\u6B21\u6027\u5DE5\u4F5C\u5E3D\u3001\u62A4\u76EE\u955C/\u9632\u62A4\u9762\u5C4F\u3001\u533B\u7528\u9632\u62A4\u53E3\u7F69(N95) \u3001\u9632\u62A4\u670D/\u4E00\u6B21\u6027\u9632\u6E17\u900F\u9694\u79BB\u8863\u548C\u4E00\u6B21\u6027\u4E73\u80F6\u624B\u5957\uFF0C\u5FC5\u8981\u65F6\u7A7F\u4E00\u6B21\u6027\u978B\u5957; \u540C\u65F6\u5E94\u51C6\u5907\u4E09\u7EA7\u9632\u62A4\u7528\u54C1\uFF0C\u5F53\u6709\u53EF\u80FD\u53D1\u751F\u60A3\u8005\u547C\u5438\u5206\u6CCC\u7269\u3001\u4F53\u5185\u7269\u8D28\u55B7\u6E85\u65F6\u5E94\u51C6\u5907\u4E09\u7EA7\u9632\u62A4\u3002</p><h4 id="_2-1-2-\u8F6C\u8FD0\u8BBE\u5907\u548C\u836F\u54C1" tabindex="-1"><a class="header-anchor" href="#_2-1-2-\u8F6C\u8FD0\u8BBE\u5907\u548C\u836F\u54C1" aria-hidden="true">#</a> 2.1.2\u2003\u8F6C\u8FD0\u8BBE\u5907\u548C\u836F\u54C1</h4><p>(1) \u914D\u7F6E\u4FBF\u643A\u76D1\u62A4\u4EEA\u3001\u6C27\u6C14\u74F6\u3001\u8F6C\u8FD0\u547C\u5438\u673A\u3001\u591A\u529F\u80FD\u9664\u98A4\u8D77\u640F\u5668\u3001\u5FAE\u91CF\u6CE8\u5C04\u6CF5\u7B49\uFF0C\u5982\u8F6C\u8FD0\u65F6\u95F4\u8F83\u957F\u6216\u5FC5\u8981\u65F6\u5E94\u643A\u5E26\u4FBF\u643A\u5F0F\u7535\u52A8\u5438\u75F0\u673A\u3002\u8F6C\u8FD0\u4EBA\u5458\u5E94\u643A\u5E26\u624B\u673A\u3001\u5BF9\u8BB2\u673A\u7B49\u901A\u8BAF\u8BBE\u5907\uFF0C\u4EE5\u5E94\u5BF9\u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u51FA\u73B0\u7A81\u53D1\u4E8B\u4EF6\u65F6\u4E0E\u75C5\u623F\u53D6\u5F97\u8054\u7CFB\u3002</p><p>(2) \u914D\u5907\u53E3\u54BD\u901A\u6C14\u7BA1\u548C\u5E26\u6709\u547C\u6C14\u7EC8\u672B\u6B63\u538B\u9600\u95E8\u7684\u7B80\u6613\u547C\u5438\u7403\u56CA\uFF0C\u540C\u65F6\u643A\u5E26\u6C14\u7BA1\u63D2\u7BA1\u76D2(\u5305\u62EC\u53E3\u54BD\u901A\u6C14\u7BA1\u3001\u5404\u578B\u53F7\u6C14\u7BA1\u63D2\u7BA1\u5BFC\u7BA1\u3001\u5589\u955C\u3001\u5BFC\u4E1D\u3001\u7B80\u6613\u547C\u5438\u5668\u3001\u6CE8\u5C04\u5668\u3001\u56FA\u5B9A\u5E26\u7B49) \u3002</p><p>(3) \u8F6C\u8FD0\u8BBE\u5907\u5E94\u8FDE\u63A5\u5B8C\u6574\uFF0C\u7535\u91CF\u53CA\u6C27\u6C14\u5145\u8DB3\uFF0C \u5747\u5904\u4E8E\u5B8C\u597D\u5907\u7528\u72B6\u6001\u3002</p><p>(4) \u51C6\u5907\u5E38\u7528\u6025\u6551\u836F\u54C1\u53CA\u63D2\u7BA1\u836F\u54C1\uFF0C\u5305\u62EC\u80BE\u4E0A\u817A\u7D20\u3001\u591A\u5DF4\u80FA\u3001\u80FA\u7898\u916E\u3001\u54AA\u8FBE\u5511\u4ED1\u3001\u5229\u591A\u5361\u56E0\u3001\u963F\u6258\u54C1\u3001\u7F57\u5E93\u6EB4\u94F5\u3001\u7425\u73C0\u9170\u80C6\u78B1\u7B49\u3002</p><p>(5) \u51C6\u5907\u7B26\u5408\u8F6C\u8FD0\u8981\u6C42\u7684\u8F6C\u8FD0\u5E8A\u3002</p><h4 id="_2-1-3-\u60A3\u8005\u8BC4\u4F30" tabindex="-1"><a class="header-anchor" href="#_2-1-3-\u60A3\u8005\u8BC4\u4F30" aria-hidden="true">#</a> 2.1.3\u2003\u60A3\u8005\u8BC4\u4F30</h4><p>(1) \u8BC4\u4F30\u60A3\u8005\u751F\u547D\u4F53\u5F81\u3001\u547C\u5438\u578B\u6001\u53CA\u914D\u5408\u7A0B\u5EA6; \u8BC4\u4F30\u60A3\u8005\u6C14\u9053\u53CA\u53E3\u9F3B\u8154\u5206\u6CCC\u7269\uFF0C\u53CA\u65F6\u6E05\u7406; \u8BC4\u4F30\u60A3\u8005\u610F\u8BC6\uFF0C\u8E81\u52A8\u8005\u5E94\u7ED9\u4E88\u5FC5\u8981\u7684\u9547\u75DB\u3001\u9547\u9759\u548C\u4FDD\u62A4\u6027\u7EA6\u675F\u3002</p><p>(2) \u8F6C\u8FD0\u524D\u4E3B\u7BA1\u533B\u751F\u53CA\u62A4\u58EB\u5E94\u540C\u65F6\u8BC4\u4F30\u60A3\u8005\u5FAA\u73AF\u53CA\u8840\u6D41\u52A8\u529B\u5B66\u662F\u5426\u9002\u5408\u8F6C\u8FD0\uFF0C\u98CE\u9669\u8F83\u5927\u65F6\u5E94\u91CD\u65B0\u8BC4\u4F30\u8F6C\u8FD0\u5FC5\u8981\u6027\u3002</p><h4 id="_2-1-4-\u60A3\u8005\u51C6\u5907" tabindex="-1"><a class="header-anchor" href="#_2-1-4-\u60A3\u8005\u51C6\u5907" aria-hidden="true">#</a> 2.1.4\u2003\u60A3\u8005\u51C6\u5907</h4><p>(1) \u4E3A\u60A3\u8005\u4F69\u5E26\u8155\u5E26\u7B49\u8EAB\u4EFD\u8BC6\u522B\u6807\u8BC6\u5E76\u6838\u5BF9\u60A3\u8005\u8EAB\u4EFD\u3002</p><p>(2) \u4FDD\u6301\u4E24\u6761\u9759\u8109\u901A\u8DEF\u901A\u7545\uFF0C\u4EE5\u4FBF\u7ED9\u4E88\u8840\u7BA1\u6D3B\u6027\u836F\u7269\u6301\u7EED\u8F93\u6CE8\u53CA\u4E34\u65F6\u836F\u7269\u8F93\u6CE8\u3002</p><p>(3) \u5E94\u7528\u836F\u7269\u63A7\u5236\u4E0D\u5229\u4E8E\u60A3\u8005\u8F6C\u8FD0\u7684\u539F\u53D1\u75BE\u75C5\u3002</p><p>(4) \u6C14\u80F8\u53CA\u5927\u91CF\u80F8\u8154\u79EF\u6DB2\u7684\u60A3\u8005\u653E\u7F6E\u80F8\u8154\u95ED\u5F0F\u5F15\u6D41\u3002</p><p>(5) \u8F6C\u8FD0\u65F6\u95F4\u8F83\u957F\u6216\u4F7F\u7528\u5229\u5C3F\u5242\u7684\u60A3\u8005\uFF0C\u7ED9\u4E88\u7559\u7F6E\u5C3F\u7BA1\u3002</p><p>(6) \u59A5\u5584\u56FA\u5B9A\u5404\u79CD\u7BA1\u8DEF\uFF0C\u9632\u6B62\u56E0\u70E6\u8E81\u53CA\u4F53\u4F4D\u53D8\u5316\u53D1\u751F\u8131\u843D\u3001\u626D\u66F2\u3001\u53D7\u538B\u3001\u963B\u585E\u7B49\u3002</p><p>(7) \u521B\u4F24\u60A3\u8005\u4F7F\u7528\u9888\u6258\u7B49\u4FDD\u6301\u810A\u67F1\u7A33\u5B9A\uFF0C\u957F\u9AA8\u9AA8\u6298\u884C\u5939\u677F\u56FA\u5B9A\u3002</p><h4 id="_2-1-5-\u8F6C\u8FD0\u524D\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#_2-1-5-\u8F6C\u8FD0\u524D\u6C9F\u901A" aria-hidden="true">#</a> 2.1.5\u2003\u8F6C\u8FD0\u524D\u6C9F\u901A</h4><p>(1) \u5411\u6E05\u9192\u60A3\u8005\u5145\u5206\u89E3\u91CA\u8F6C\u8FD0\u7684\u76EE\u7684\uFF0C\u53D6\u5F97\u60A3\u8005\u6216\u5BB6\u5C5E\u7684\u77E5\u60C5\u540C\u610F\u3002</p><p>(2) \u8F6C\u8FD0\u4EBA\u5458\u5E94\u63D0\u524D\u786E\u5B9A\u9694\u79BB\u533A\u60A3\u8005\u8F6C\u8FD0\u8DEF\u7EBF\u53CA\u6D41\u7A0B\uFF0C\u5C3D\u91CF\u7F29\u77ED\u8F6C\u8FD0\u65F6\u95F4\u3002</p><p>(3) \u5E94\u63D0\u524D\u7535\u8BDD\u8054\u7CFB\u5BF9\u63A5\u79D1\u5BA4\u505A\u597D\u63A5\u8BCA\u51C6\u5907\uFF0C \u544A\u77E5\u63A5\u6536\u90E8\u95E8\u60A3\u8005\u7684\u57FA\u672C\u60C5\u51B5\u53CA\u7279\u6B8A\u6CE8\u610F\u4E8B\u9879\uFF0C\u5E76\u843D\u5B9E\u8F6C\u8FD0\u8DEF\u7EBF\u3002 \u5EFA\u8BAE\u53C2\u4E0E\u8F6C\u8FD0\u7684\u533B\u751F\u548C\u62A4\u58EB\u6839\u636E\u65B0\u51A0\u80BA\u708E\u60A3\u8005\u8F6C\u8FD0\u6838\u67E5\u8868(\u8868 1) \u9010\u9879\u68C0\u67E5\u662F\u5426\u5DF2\u505A\u597D\u5B8C\u5584\u7684\u8F6C\u8FD0\u51C6\u5907\u3002</p><h3 id="_2-2-\u8F6C\u8FD0\u4E2D" tabindex="-1"><a class="header-anchor" href="#_2-2-\u8F6C\u8FD0\u4E2D" aria-hidden="true">#</a> 2.2\u2003\u8F6C\u8FD0\u4E2D</h3><h4 id="_2-2-1-\u4E00\u822C\u63AA\u65BD" tabindex="-1"><a class="header-anchor" href="#_2-2-1-\u4E00\u822C\u63AA\u65BD" aria-hidden="true">#</a> 2.2.1\u2003\u4E00\u822C\u63AA\u65BD</h4><p>(1) \u4F53\u4F4D\u53CA\u4FDD\u62A4: \u5728\u75C5\u60C5\u5141\u8BB8\u7684\u60C5\u51B5\u4E0B\uFF0C\u7ED9\u4E88\u60A3\u8005\u534A\u5750\u5367\u4F4D\uFF0C\u5E8A\u5934\u62AC\u9AD8 30\xB0 \uFF5E 45\xB0\uFF0C\u56DB\u80A2\u52FF\u8D85\u51FA\u8F6C\u8FD0\u5E8A\u8FB9\u7F18\u3002\u660F\u8FF7\u53CA\u8E81\u52A8\u60A3\u8005\u5E94\u4F7F\u7528\u5E8A\u6863\u548C\u7EA6\u675F\u5E26\u4FDD\u62A4\u3002\u8F6C\u8FD0\u884C\u8FDB\u5E94\u5300\u901F\u5E73\u7A33\uFF0C\u9632\u6B62\u78B0\u649E\uFF0C\u4E0A\u4E0B\u5761\u65F6\u4FDD\u6301\u60A3\u8005\u5934\u4FA7\u5728\u4E0A\u65B9\u3002</p><p>(2) \u751F\u547D\u4F53\u5F81\u76D1\u6D4B: \u8F6C\u8FD0\u4EBA\u5458\u5E94\u5206\u5DE5\u660E\u786E\uFF0C\u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u76D1\u62A4\u4EEA\u9762\u5411\u533B\u62A4\u4EBA\u5458\uFF0C\u5BC6\u5207\u76D1\u6D4B\u60A3\u8005\u5FC3\u7387\u3001 \u8840\u538B\u3001SpO2\u3001\u547C\u5438\u9891\u7387\u7684\u6CE2\u52A8\uFF0C\u89C2\u5BDF\u547C\u5438\u673A\u6CE2\u5F62\u3001\u6C14\u9053\u538B\u529B\u53CA\u6F6E\u6C14\u91CF\u7B49\uFF0C\u76D1\u6D4B\u547C\u5438\u673A\u62A5\u8B66\uFF0C\u53CA\u65F6\u5224\u65AD\u548C\u6392\u9664\u6545\u969C\u3002\u540C\u65F6\u5E94\u89C2\u5BDF\u60A3\u8005\u81EA\u4E3B\u547C\u5438\u7684\u9891\u7387\u3001\u8282\u5F8B\u548C\u547C\u5438\u673A\u662F\u5426\u540C\u6B65\uFF0C\u673A\u68B0\u901A\u6C14\u540E\u901A\u6C14\u91CF\u662F\u5426\u6070\u5F53\u7B49\uFF0C\u5982\u6709\u5F02\u5E38\u53CA\u65F6\u5904\u7406\u3002</p><p>(3) \u4FDD\u6301\u547C\u5438\u9053\u901A\u7545: \u8F6C\u8FD0\u524D\u5E94\u68C0\u67E5\u4EBA\u5DE5\u6C14\u9053\u6C14\u56CA\u538B\u529B\u5904\u4E8E 25 \uFF5E 30 cm H<sub>2</sub>O (1 cm H<sub>2</sub>O = 0.098 kPa) \uFF0C \u53CA\u65F6\u6E05\u9664\u58F0\u95E8\u4E0B\u5206\u6CCC\u7269\uFF0C\u5145\u5206\u62BD\u51FA\u80C3\u5185\u5BB9\u7269\u3002\u6CE8\u610F\u89C2\u5BDF\u60A3\u8005\u6709\u65E0\u545B\u54B3\uFF0C\u5982\u9700\u5438\u75F0\uFF0C\u5E94\u6CE8\u610F\u91C7\u7528\u5BC6\u95ED\u5438\u75F0\u88C5\u7F6E\uFF0C\u9632\u6B62\u65AD\u5F00\u6C14\u7BA1\u63D2\u7BA1\u4E0E\u547C\u5438\u673A\u8FDE\u63A5\u3002</p><p>(4) \u7BA1\u8DEF\u7684\u7BA1\u7406: \u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u5E94\u5BC6\u5207\u89C2\u5BDF\u7BA1\u8DEF\u7684\u4F4D\u7F6E\u662F\u5426\u53D1\u751F\u53D8\u5316\uFF0C\u6DB2\u4F53\u901A\u8DEF\u662F\u5426\u901A\u7545\uFF0C\u907F\u514D\u7275\u62C9\u6216\u6253\u6298\u3002\u5F15\u6D41\u74F6\u6216\u5F15\u6D41\u888B\u5E94\u4FDD\u6301\u5728\u60A3\u8005\u8EAB\u4F53\u5E73\u9762\u4E0B\u65B9\uFF0C\u5FC5\u8981\u65F6\u53EF\u77ED\u6682\u5939\u95ED\u5F15\u6D41\u7BA1\u3002\u4E2D\u5FC3\u9759\u8109\u7BA1\u8DEF\u5E94\u66B4\u9732\u5728\u8F6C\u8FD0\u8005\u7684\u89C6\u91CE\u4E2D\u4EE5\u65B9\u4FBF\u968F\u65F6\u68C0\u67E5\u3002</p><p>(5) ECMO \u7684\u62A4\u7406: \u2460\u59A5\u5584\u56FA\u5B9A\u5BFC\u7BA1\uFF0C\u907F\u514D\u7275\u62C9\u3001\u79FB\u4F4D\u3001\u6253\u6298\u7B49; \u2461\u5BC6\u5207\u5173\u6CE8\u60A3\u8005\u751F\u547D\u4F53\u5F81\u53CA\u5FAA\u73AF\u6CE2\u52A8; \u2462\u5BC6\u5207\u76D1\u6D4B ECMO \u8F6C\u901F\u3001\u6D41\u91CF\u53C2\u6570\u53CA\u6C27\u6C14\u7BA1\u7684\u8FDE\u63A5\uFF0C\u6761\u4EF6\u5141\u8BB8\u7684\u60C5\u51B5\u4E0B\u5728\u51FA\u5BA4\u524D\u6D82\u62B9\u8026\u5408\u5242\u3002</p><p>(6) \u60A3\u8005\u610F\u8BC6\u53CA\u60C5\u7EEA\u7BA1\u7406: \u5E94\u89C2\u5BDF\u60A3\u8005\u7684\u9547\u9759/ \u9547\u75DB\u72B6\u6001\uFF0C\u5FC5\u8981\u65F6\u589E\u52A0\u9547\u9759/\u9547\u75DB\u836F\u7269\u7684\u5242\u91CF\u3002\u9002\u5F53\u4E0E\u6E05\u9192\u60A3\u8005\u8FDB\u884C\u6C9F\u901A\uFF0C\u7F13\u89E3\u60A3\u8005\u7D27\u5F20\u3001\u7126\u8651\u60C5\u7EEA\u3002</p><p>(7) \u5176\u4ED6: \u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u5783\u573E\u5E94\u7528\u53CC\u5C42\u9EC4\u8272\u5783\u573E\u888B\u5305\u624E\u3002</p><h4 id="_2-2-2-\u6F5C\u5728\u95EE\u9898\u53CA\u5176\u539F\u56E0\u8BC6\u522B" tabindex="-1"><a class="header-anchor" href="#_2-2-2-\u6F5C\u5728\u95EE\u9898\u53CA\u5176\u539F\u56E0\u8BC6\u522B" aria-hidden="true">#</a> 2.2.2\u2003\u6F5C\u5728\u95EE\u9898\u53CA\u5176\u539F\u56E0\u8BC6\u522B</h4><p>(1) \u6E05\u7406\u547C\u5438\u9053\u65E0\u6548: \u53EF\u80FD\u4E0E\u6C14\u7BA1\u63D2\u7BA1\u4F4D\u7F6E\u3001 \u6C14\u9053\u5206\u6CCC\u7269\u589E\u591A\u76F8\u5173\u3002</p><p>(2) \u6C14\u4F53\u4EA4\u6362\u53D7\u635F: \u53EF\u80FD\u4E0E\u60A3\u8005\u80BA\u90E8\u75BE\u75C5\u3001\u80BA\u987A\u5E94\u6027\u4E0B\u964D\u76F8\u5173\u3002</p><p>(3) \u5FC3\u8F93\u51FA\u91CF\u51CF\u5C11: \u53EF\u80FD\u4E0E\u60A3\u8005\u611F\u67D3\u3001\u5FAA\u73AF\u4E0D\u7A33\u5B9A\u76F8\u5173\u3002</p><p>(4) \u6709\u8BEF\u5438\u7684\u5371\u9669: \u53EF\u80FD\u4E0E\u60A3\u8005\u6C14\u9053\u5206\u6CCC\u7269\u589E\u591A\u3001\u6E05\u7406\u547C\u5438\u9053\u4F4E\u6548\u76F8\u5173\u3002</p><p>(5) \u6709\u53D7\u4F24\u7684\u5371\u9669: \u53EF\u80FD\u4E0E\u8F6C\u8FD0\u8FC7\u7A0B\u53CA\u9547\u9759\u836F\u7269\u4F7F\u7528\u76F8\u5173\u3002</p><p>(6) \u6F5C\u5728\u5E76\u53D1\u75C7: \u5FC3\u6E90\u6027\u4F11\u514B\u3001\u5FC3\u5F8B\u5931\u5E38\u3001\u81EA\u53D1\u6027\u6C14\u80F8\u3001\u51FA\u8840\u3001\u7A92\u606F\u3002</p><h4 id="_2-2-3-\u7A81\u53D1\u4E8B\u4EF6\u7684\u5904\u7406" tabindex="-1"><a class="header-anchor" href="#_2-2-3-\u7A81\u53D1\u4E8B\u4EF6\u7684\u5904\u7406" aria-hidden="true">#</a> 2.2.3 \u7A81\u53D1\u4E8B\u4EF6\u7684\u5904\u7406</h4><p>(1) \u6C14\u7BA1\u63D2\u7BA1\u8131\u51FA: \u7ACB\u5373\u5C06\u7B80\u6613\u547C\u5438\u5668\u8FDE\u63A5\u6C27\u6C14\u4E0E\u9762\u7F69\uFF0CEC \u624B\u6CD5\u3010\u5DE6\u624B\u62C7\u6307\u548C\u98DF\u6307\u6210 C \u5F62\u6309\u4F4F\u9762\u7F69\uFF0C\u4E2D\u6307\u548C\u65E0\u540D\u6307\u6258\u4F4F\u60A3\u8005\u4E0B\u988C\u3002\u4E2D\u6307\u3001\u65E0\u540D\u6307\u3001\u5C0F\u6307(\u6784\u6210\u201CE\u201D \u5B57) \u94A9\u4F4F\u4E0B\u988C\uFF0C\u6253\u5F00\u6C14\u9053; \u62C7\u6307\u3001\u98DF\u6307(\u6784\u6210\u201CC\u201D\u5B57) \u56FA\u5B9A\u9762\u7F69\u3011 \u5F00\u653E\u60A3\u8005\u6C14\u9053\u4E88\u60A3\u8005\u7B80\u6613\u547C\u5438\u5668\u8F85\u52A9\u547C\u5438\uFF0C\u5BC6\u5207\u89C2\u5BDF\u60A3\u8005\u751F\u547D\u4F53\u5F81\uFF0C\u4FDD\u8BC1\u60A3\u8005\u5B89\u5168\u7684\u540C\u65F6\u8BC4\u4F30\u5468\u56F4\u60C5\u51B5\u540E\u53EF\u5C31\u5730\u884C\u6C14\u7BA1\u63D2\u7BA1\u672F\u6216\u8FC5\u901F\u524D\u5F80\u6700\u8FD1\u7684\u75C5\u623F\u7D27\u6025\u884C\u6C14\u7BA1\u63D2\u7BA1\u672F\u3002</p><p>(2) \u6076\u6027\u5FC3\u5F8B\u5931\u5E38\u4E0E\u5FC3\u8DF3\u9AA4\u505C: \u7ACB\u5373\u4E88\u4EE5\u60A3\u8005 CPR\uFF0C\u63A8\u6CE8\u80BE\u4E0A\u817A\u7D20\uFF0C\u5FC5\u8981\u65F6\u53EF\u4E88\u60A3\u8005\u7535\u9664\u98A4\uFF0C\u7EF4\u6301\u60A3\u8005\u5FAA\u73AF\uFF0C\u5BFB\u627E\u6700\u8FD1\u7684\u914D\u6709\u62A2\u6551\u8F66\u7684\u75C5\u623F\u8FDB\u884C\u8FDB\u4E00\u6B65\u62A2\u6551\uFF0C\u590D\u82CF\u6210\u529F\u540E\u8FD4\u56DE ICU \u4E88\u4EE5\u9AD8\u7EA7\u751F\u547D\u652F\u6301\u3002</p><p>(3) \u5F15\u6D41\u7BA1\u8131\u51FA: \u7ACB\u5373\u5BC6\u5207\u76D1\u6D4B\u60A3\u8005\u751F\u547D\u4F53\u5F81\uFF0C \u89C2\u5BDF\u60A3\u8005\u6709\u65E0\u5FAA\u73AF\u6CE2\u52A8\uFF0C\u82E5\u80F8\u8154\u95ED\u5F0F\u5F15\u6D41\u8131\u51FA\uFF0C\u7ACB\u5373\u7528\u7EB1\u5E03\u8986\u76D6\u4F24\u53E3\uFF0C\u907F\u514D\u53D1\u751F\u6C14\u80F8\uFF0C\u540C\u65F6\u8FD4\u56DE ICU \u75C5\u623F\u91CD\u65B0\u7F6E\u7BA1\u5E76\u7EE7\u7EED\u89C2\u5BDF\u3002</p><p>(4) \u5FAA\u73AF\u6CE2\u52A8: \u7ACB\u5373\u68C0\u67E5\u8840\u7BA1\u6D3B\u6027\u836F\u7269\u901A\u8DEF\u662F\u5426\u901A\u7545\uFF0C\u82E5\u9047\u7BA1\u8DEF\u6253\u6298\uFF0C\u5E94\u5148\u65AD\u5F00\u518D\u91CD\u65B0\u8FDE\u63A5\uFF0C\u907F\u514D\u5927\u5242\u91CF\u8840\u7BA1\u6D3B\u6027\u836F\u7269\u7A81\u7136\u8FDB\u5165\u60A3\u8005\u8EAB\u4F53\u5BFC\u81F4\u5FAA\u73AF\u5267\u70C8\u6CE2\u52A8\u3002</p><p>(5) \u4EEA\u5668\u6545\u969C:</p><p>\u2460\u547C\u5438\u673A\u51FA\u73B0\u62A5\u8B66\u540E\u5E94\u7ACB\u5373\u5224\u65AD\u62A5\u8B66\u539F\u56E0\uFF0C\u82E5\u65E0\u6CD5\u7ACB\u5373\u6392\u9664\u6545\u969C\u5E94\u7ACB\u523B\u65AD\u5F00\u547C\u5438\u673A\u4E0E\u6C14\u7BA1\u63D2\u7BA1\u8FDE\u63A5\uFF0C\u8FC5\u901F\u5C06\u7B80\u6613\u547C\u5438\u5668\u8FDE\u63A5\u81F3\u6C14\u7BA1\u63D2\u7BA1\uFF0C\u4FDD\u8BC1\u60A3\u8005\u5B89\u5168\u7684\u60C5\u51B5\u4E0B\u8FD4\u56DE\u75C5\u623F\uFF0C\u8FDE\u63A5\u547C\u5438\u673A\uFF0C\u5F85\u5E73\u7A33\u540E\u518D\u884C\u8F6C\u8FD0\u3002</p><p>\u2461\u76D1\u62A4\u4EEA\u51FA\u73B0\u6545\u969C\u540E\u5E94\u7ACB\u5373\u5173\u6CE8\u60A3\u8005\u5FAA\u73AF\uFF0C\u5224\u65AD\u9888\u52A8\u8109\u640F\u52A8\u60C5\u51B5\uFF0C\u540C\u65F6\u968F\u884C\u4EBA\u5458\u5E94\u7ACB\u534F\u52A9\u8FDE\u63A5\u5907\u7528\u76D1\u62A4\u4EEA\u3002</p><p>\u2462\u6CE8\u5C04\u6CF5\u51FA\u73B0\u6545\u969C\u65F6\u5E94\u7ACB\u5373\u66F4\u6362\uFF0C\u540C\u65F6\u5173\u95ED\u6CF5\u5165\u8840\u7BA1\u6D3B\u6027\u836F\u7269\u4E09\u901A\uFF0C\u9632\u6B62\u836F\u7269\u63A8\u5165\u60A3\u8005\u4F53\u5185\u3002</p><p>\u2463ECMO \u51FA\u73B0\u6545\u969C\u65F6\u5E94\u7ACB\u523B\u91C7\u7528\u624B\u6447\u67C4\u542F\u52A8\u8840\u6CF5\uFF0C\u5728\u4FDD\u6301\u9002\u5F53\u8F6C\u901F\u4E0E\u6D41\u91CF\u7684\u60C5\u51B5\u4E0B\u8FC5\u901F\u524D\u5F80\u6700\u8FD1\u7684\u75C5\u623F\u8FDB\u4E00\u6B65\u5904\u7406\u3002</p><p>(6) \u8F6C\u8FD0\u4EBA\u5458\u804C\u4E1A\u66B4\u9732: \u4E00\u65E6\u8F6C\u8FD0\u4EBA\u5458\u5728\u8F6C\u8FD0\u8FC7\u7A0B\u4E2D\u51FA\u73B0\u804C\u4E1A\u66B4\u9732\uFF0C\u5E94\u7ACB\u5373\u66F4\u6362\u533B\u7528\u9632\u62A4\u7528\u54C1\uFF0C\u5E76\u901A\u77E5\u75C5\u623F\uFF0C\u91CD\u65B0\u6D3E\u51FA\u4EBA\u5458\u8FDB\u884C\u63A5\u66FF\uFF0C\u540C\u65F6\u8981\u4FDD\u969C\u60A3\u8005\u7684\u5B89\u5168\uFF0C\u4EBA\u5458\u4EA4\u66FF\u540E\u7EE7\u7EED\u8FDB\u884C\u8F6C\u8FD0\uFF0C\u66B4\u9732\u4EBA\u5458\u56DE\u5230\u75C5\u623F\u540E\u5E94\u6839\u636E\u66B4\u9732\u7B49\u7EA7\u7ACB\u5373\u7ED9\u4E88\u76F8\u5E94\u5904\u7406\u5E76\u4E0A\u62A5\u533B\u9662\u611F\u67D3\u76F8\u5173\u7BA1\u7406\u90E8\u95E8\u3002</p><h3 id="_2-3-\u8F6C\u8FD0\u540E" tabindex="-1"><a class="header-anchor" href="#_2-3-\u8F6C\u8FD0\u540E" aria-hidden="true">#</a> 2.3\u2003\u8F6C\u8FD0\u540E</h3><p>2.3.1\u2003\u8F6C\u8FD0\u5E8A\u5E8A\u7F69\u53D6\u4E0B\u5E94\u7528\u53CC\u5C42\u6A59\u8272\u5783\u573E\u888B\u5305\u624E\uFF0C\u5E76\u6807\u6CE8\u201C\u65B0\u51A0\u80BA\u708E\u611F\u67D3\uFF0C\u65E5\u671F\u201D\u7B49\u3002</p><p>2.3.2\u2003\u5E94\u7528 75%\u9152\u7CBE\u6216 5\u2030\u542B\u6C2F\u6D88\u6BD2\u5242\u55B7\u6D12\u5E76\u64E6\u62ED\u8F6C\u8FD0\u5E8A\u3002</p><p>2.3.3\u2003\u5E94\u5C06\u8F6C\u8FD0\u8BBE\u5907\u8868\u9762\u7528 75%\u9152\u7CBE\u64E6\u62ED\u540E\u5907\u7528\u3002</p><p>2.3.4\u2003\u8D1F\u8D23\u8F6C\u8FD0\u4EBA\u5458\u5E94\u91CD\u65B0\u66F4\u6362\u9694\u79BB\u8863\u3002</p><p>2.3.5\u2003\u53CA\u65F6\u8865\u5145\u8F6C\u8FD0\u4F7F\u7528\u836F\u54C1\u3002</p><p>2.3.6\u2003\u7559\u5B58\u6587\u4EF6\u5E76\u59A5\u5584\u653E\u7F6E\u3002</p><p>2.3.7\u2003\u5176\u4ED6: \u5EFA\u8BAE\u6839\u636E\u65B0\u51A0\u80BA\u708E\u60A3\u8005\u8F6C\u8FD0\u6838\u67E5\u8868(\u8868 1) \u9010\u9879\u68C0\u67E5\u662F\u5426\u5904\u7F6E\u59A5\u5F53\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/seasideccm/img/upload/1620336980541-image-20210507053620447.png" alt=""></p>',72);function i(t,n){return r}var h=e(p,[["render",i],["__file","\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u5371\u91CD\u75C7\u60A3\u8005\u9662\u5185\u8F6C\u8FD0\u4E13\u5BB6\u5171\u8BC6.html.vue"]]);export{h as default};
